	 * Gets the current filtered group_by.
	 *
	 * @return string
	 */
	private function get_group_by() {
		if ( isset( $this->input['group_by'] ) && in_array( $this->input['group_by'], array_keys( $this->allowed_group_by() ) ) ) {
			return $this->input['group_by'];
